PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE proxy_responses (
    id INTEGER PRIMARY KEY,
    active INTEGER DEFAULT 1,
    context TEXT,
    options BLOG
  );
CREATE TABLE use_case_to_use_cases (
          id  INTEGER PRIMARY KEY,
          use_case_id INTEGER NOT NULL,
          child_use_case_id INTEGER NOT NULL,
          sequence INTEGER NOT NULL
        );
CREATE TABLE use_case_to_mock_responses (
          id  INTEGER PRIMARY KEY,
          use_case_id INTEGER NOT NULL,
          mock_response_id INTEGER NOT NULL,
          sequence INTEGER NOT NULL
        );
CREATE TABLE mock_responses (
          id	INTEGER,
          name	TEXT DEFAULT 'Unnamed',
          req_url	TEXT,
          req_method	TEXT DEFAULT 'GET',
          req_payload	TEXT,
          res_status	INTEGET DEFAULT 200,
          res_delay_sec	INTEGER,
          res_content_type	TEXT DEFAULT 'application/json',
          res_body	BLOB,
          created_at	INTEGER,
          created_by	string,
          updated_at	INTEGER,
          updated_by	string,
          PRIMARY KEY(id)
        );
CREATE TABLE use_cases (
          id  INTEGER PRIMARY KEY,
          name  TEXT NOT NULL,
          description TEXT NOT NULL,
          created_at	INTEGER,
          created_by	string,
          updated_at	INTEGER,
          updated_by	string
        );
COMMIT;
